import 'regenerator-runtime/runtime'
import { find, isArray, isBoolean, isFunction, isNumber } from 'lodash'
import * as Ajv from 'ajv'

import providers from './providers'
import BlockSchema from './schema/Block.json'
import TransactionSchema from './schema/Transaction.json'

export default class Client {
  /**
   * ChainAbstractionLayer client
   * @param {object} [provider] - The provider instance.
   * @param {string} [version] - Version string
   */
  constructor (provider, version) {
    if (provider) {
      this.addProvider(provider)
    }

    if (version) {
      /**
       * @type {string}
       */
      this.version = version
    }

    const ajv = new Ajv()
    this.validateTransaction = ajv.compile(TransactionSchema)
    this.validateBlock = ajv.compile(BlockSchema)
  }

  /**
   * Add a provider.
   * @param {object} provider - The provider instance.
   */
  addProvider (provider) {
    /**
     * @type {object}
     */
    this.provider = provider
  }

  _checkMethod (method) {
    if (!this.provider) {
      throw new Error('No provider provided')
    }

    if (!isFunction(this.provider[method])) {
      throw new Error(`Unimplemented method: ${method}`)
    }

    if (isFunction(this.provider._checkMethodVersionSupport)) {
      if (!this.provider._checkMethodVersionSupport(method, this.version)) {
        throw new Error(`Method "${method}" is not supported by version "${this.version}"`)
      }
    }

    if (!isFunction(this.provider[method])) {
      throw new Error(`Unimplemented method: ${method}`)
    }
  }

  /**
   * Generate a block
   * @param {!number} numberOfBlocks - Number of blocks to be generated
   * @return {Promise<string[], Error>} Returns a promise with Block hash of the
   *  generated blocks if resolved. Throws an Error if rejected.
   */
  async generateBlock (numberOfBlocks) {
    this._checkMethod('generateBlock')

    if (!isNumber(numberOfBlocks)) {
      throw new Error('Invalid number of blocks to be generated')
    }

    const blockHashes = await this.provider.generateBlock(numberOfBlocks)

    if (!isArray(blockHashes)) {
      throw new Error('Provider returned an invalid response')
    }

    const invalidBlock = find(blockHashes, blockHash => !(/^[A-Fa-f0-9]+$/.test(blockHash)))

    if (invalidBlock) {
      throw new Error('Provider returned an invalid response')
    }

    return blockHashes
  }

  /**
   * Get block by number
   * @param {!number} blockNumber - Number of the block to be fetched
   * @param {boolean} [includeTx=false] - If true, fetches transaction in the block
   * @return {Client.schemas.Block} Returns a Block
   */
  async getBlockByNumber (blockNumber, includeTx = false) {
    this._checkMethod('getBlockByNumber')

    if (!isNumber(blockNumber)) {
      throw new Error('Invalid Block number')
    }

    if (!isBoolean(includeTx)) {
      throw new Error('Second parameter should be boolean')
    }

    const block = await this.provider.getBlockByNumber(blockNumber, includeTx)

    if (!this.validateBlock(block)) {
      throw new Error('Provider returned an invalid block')
    }

    return block
  }

  async getBlockHeight () {
    this._checkMethod('getBlockHeight')

    const blockHeight = await this.provider.getBlockHeight()

    if (!isNumber(blockHeight)) {
      throw new Error('Provider returned an invalid block height')
    }

    return blockHeight
  }

  async getTransactionByHash (txHash) {
    this._checkMethod('getTransactionByHash')

    if (!(/^[A-Fa-f0-9]$/.test(txHash))) {
      throw new Error('Transaction hash should be a valid hex string')
    }

    const transaction = await this.provider.getTransactionByHash(txHash)

    if (!this.validateTransaction(transaction)) {
      throw new Error('Provider returned an invalid transaction')
    }

    return transaction
  }

  async getAddresses () {
    this._checkMethod('getAddresses')

    const addresses = await this.provider.getAddresses()

    if (!isArray(addresses)) {
      throw new Error('Provider returned an invalid response')
    }

    return addresses
  }

  async signMessage (message, from) {
    this._checkMethod('signMessage')

    const signedMessage = await this.provider.signMessage(message, from)

    return signedMessage
  }
}

Client.providers = providers
Client.schemas = {
  Block: BlockSchema,
  Transaction: TransactionSchema
}

import JSONBigInt from 'json-bigint'
import { has } from 'lodash'

const { parse } = JSONBigInt({ storeAsString: true, strict: true })

function prepareRequest ({ method, params }) {
  return JSON.stringify({
    id: Date.now(),
    method: method,
    params: params
  })
}

function praseResponse (body, headers) {
  if (typeof body === 'string' && headers['content-type'] !== 'application/json') {
    throw new Error(body)
  }

  body = parse(body)

  if (body.error) {
    throw new Error(`Error occurred while processing the RPC call: ${body.error}`)
  }

  if (!has(body, 'result')) {
    throw new Error('Missing `result` on the RPC call result')
  }

  return body.result
}

export {
  prepareRequest,
  praseResponse
}

import BitcoinRPCProvider from './BitcoinRPCProvider'

import Transport from '@ledgerhq/hw-transport-u2f'
// import Transport from '@ledgerhq/hw-transport-node-hid'
import LedgerBtc from '@ledgerhq/hw-app-btc'

export default class BitcoinLedgerProvider extends BitcoinRPCProvider {
  constructor (uri, user, pass) {
    super(uri, user, pass)
    this._ledgerBtc = false
    this._derivationPath = `44'/0'/0'/0`
  }

  async _connectToLedger () {
    if (!this._ledgerBtc) {
      const transport = await Transport.create()
      this._ledgerBtc = new LedgerBtc(transport)
    }
  }

  async _updateDerivationPath (path) {
    this._derivationPath = path
  }

  async getAddresses () {
    await this._connectToLedger()

    const { bitcoinAddress } = await this._ledgerBtc.getWalletPublicKey(this._derivationPath)

    return [ bitcoinAddress ]
  }

  async signMessage (message, from) {
    await this._connectToLedger()

    const hex = Buffer.from(message).toString('hex')

    return this._ledgerBtc.signMessageNew(this._derivationPath, hex)
  }
}

import axios from 'axios'

import { prepareRequest, praseResponse } from '../JsonRpcHelper'

export default class BitcoinRPCProvider {
  constructor (uri, user, pass) {
    this.axios = axios.create({
      baseURL: uri,
      transformRequest: [({ data }, headers) => prepareRequest(data)],
      transformResponse: [(data, headers) => praseResponse(data, headers)],
      validateStatus: (status) => status === 200
    })

    if (user || pass) {
      this.axios.defaults.auth = {
        username: user,
        password: pass
      }
    }
  }

  _rpc (method, ...params) {
    return this.axios.post('/', {
      data: { method, params }
    }).then(({ data }) => data)
  }

  async generateBlock (numberOfBlocks) {
    return this._rpc('generate', numberOfBlocks)
  }
}

import EthereumRPCProvider from './EthereumRPCProvider'

import Transport from '@ledgerhq/hw-transport-u2f'
// import Transport from '@ledgerhq/hw-transport-node-hid'
import LedgerEth from '@ledgerhq/hw-app-eth'

export default class EthereumLedgerProvider extends EthereumRPCProvider {
  constructor (uri, user, pass) {
    super(uri, user, pass)
    this._ledgerEth = false
    this._derivationPath = `44'/60'/0'/0'/0`
  }

  async _connectToLedger () {
    if (!this._ledgerEth) {
      const transport = await Transport.create()
      this._ledgerEth = new LedgerEth(transport)
    }
  }

  async _updateDerivationPath (path) {
    this._derivationPath = path
  }

  async getAddresses () {
    await this._connectToLedger()

    const { address } = await this._ledgerEth.getAddress(this._derivationPath)

    return [ address ]
  }

  async signMessage (message) {
    await this._connectToLedger()

    const hex = Buffer.from(message).toString('hex')

    return this._ledgerEth.signPersonalMessage(this._derivationPath, hex)
  }
}

import { isFunction } from 'lodash'

export default class EthereumMetaMaskProvider {
  constructor (metamaskProvider) {
    if (!isFunction(metamaskProvider.sendAsync)) {
      throw new Error('Invalid MetaMask Provider')
    }

    this._metamaskProvider = metamaskProvider
  }

  _toMM (method, ...params) {
    return new Promise((resolve, reject) => {
      this
        ._metamaskProvider
        .sendAsync({ method, params }, (err, data) => {
          if (err) {
            reject(err)
            return
          }

          if (!data) {
            reject(new Error('Something went wrong'))
            return
          }

          if (!data.result) {
            reject(new Error('Something went wrong'))
            return
          }

          resolve(data.result)
        })
    })
  }

  async getAddresses () {
    return this._toMM('eth_accounts')
  }

  async signMessage (message, from) {
    const hex = Buffer.from(message).toString('hex')

    return this._toMM('personal_sign', `0x${hex}`, from)
  }
}

